import React, { Component } from 'react';
import axios from 'axios'

class News extends Component {
    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          items: []
        };
    }

    componentDidMount() {
      this.interval = setInterval(() => {
        axios.get("https://newsapi.org/v2/top-headlines?sources=techcrunch&apiKey=51e658e65c9d4805a7a96bfc19526e55")
          .then(res => {
            console.log(res)
              const data = res.data.articles;
              this.setState({isLoaded: true, items: data})
              console.log("get the news!");
            },
            (error) => {
              this.setState({
                isLoaded: true,
                error
              })
            })
      }, 5000)

      /*this.interval = setInterval(() => {
        axios.get("https://newsapi.org/v2/top-headlines?sources=techcrunch&apiKey=51e658e65c9d4805a7a96bfc19526e55")
            .then(res => {
              console.log(res)
                const data = res.data.articles;
                this.setState({isLoaded: true, items: data})
                console.log("updated");
              },
              (error) => {
                this.setState({
                  isLoaded: true,
                  error
                })
              })
      }, 60000)*/

    }

    componentWillUnmount() {
      this.res = true
    }

    render() {
      const { error, isLoaded, items } = this.state;
          if (error) {
            return <div>Error: {error.message}</div>;
          } else if (!isLoaded) {
              return <div>Loading...</div>;
          } else {
            console.log(this.state.items);
              return (
                <div>
                  {items.map(item => (
                    <div key={item.title}>
                      <div className="news-title">{item.title}</div>
                      <a href= {item.url} target="_blank">
                        <img src= {item.urlToImage} alt="news story"></img>
                      </a>
                    </div>
                  ))}
                </div>
              );
            }

    }

    /*shouldComponentUpdate(nextState) {
      console.log("updated in shouldComponentUpdate");
      return nextState.items !== this.state.items;
    }*/
}

export default News;
