import React, {Component} from 'react';
import axios from 'axios';

class Blog extends Component {
    
    state = {
        blog: null
    }

    componentDidMount() {
        axios.get('./blog.json/' + id)
            .then(res => {
                console.log(res)
                this.setState({
                    blog: res.data
                })
            })
    }
    
    render() {
        const blog  = this.state.blog ? (
            <div className="blog">
                <h4>{this.state.blog.title}</h4>
            </div>
        ) : (
            <div className="center">Loading Post...</div>
        )
        return (
            <div>{ blog }</div>
        )
    }
}

export default Blog;
