import React, { Component } from 'react';
import './App.css';
import logo from './images/jmLogo.png';
import wakeup from './images/wakeup.png';
import code from './images/coding.png';
import tech from './images/techtoday.jpg';
import Blogposts from './Blogposts';

class App extends Component {
  state = {
    blogposts: [
      { date: '04/04/2019', title: 'Why do we wake up early?', image: wakeup, id: 1},
      { date: '03/06/2019', title: 'The Benefits of Learning to Code', image: code, id: 2},
      { date: '04/26/2018', title: 'Technology Today', image: tech, id: 3}
    ]
  }
  render() {
    return (
      <div className="container">

        <header className="home-image">
          <div className="header-logo">
            <img src={logo}></img>
          </div>
          <div className="header-words">A Better Way to Blog</div>
        </header>

        <div className="main">
          <div className="main-blog">
            <Blogposts blogposts = { this.state.blogposts } />
          </div>
        </div>

        <footer className="footer-container">
          <p id="footer">Copyright @ 2019 JM.com</p>
        </footer>

        </div>
    );
  }
}

export default App;
