import React, { Component } from 'react';

class News extends Component {
    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          items: []
        };
    }

    componentDidMount() {
      fetch("https://newsapi.org/v2/top-headlines?sources=techcrunch&apiKey=51e658e65c9d4805a7a96bfc19526e55")
        .then(res => res.json())
        .then(
          (result) => {
            this.setState({
              isLoaded: true,
              items: result.articles
            });
          },
            
          (error) => {
            this.setState({
              isLoaded: true,
              error
            });
          }
        )
    }

    render() {
      const { error, isLoaded, items } = this.state;
          if (error) {
            return <div>Error: {error.message}</div>;
          } else if (!isLoaded) {
              return <div>Loading...</div>;
          } else {
            console.log(this.state.items);
              return (
                <div>
                  {items.map(item => (
                    <div key={item.title}>
                      <div>{item.title}</div>
                      <div>{item.publishedAt}</div>
                      <img src = {item.urlToImage}></img>
                    </div>
                  ))}
                </div>
              );
            }

    }
}

export default News;
