import React, { Component } from 'react';

class News extends Component {
    constructor(props) {
        super(props);
        this.state = {
          error: null,
          isLoaded: false,
          items: []
        };
    }

    componentDidMount() {
        fetch("https://newsapi.org/v2/everything?q=bitcoin&from=2019-03-10&sortBy=publishedAt&apiKey=51e658e65c9d4805a7a96bfc19526e55")
          .then(res => res.json())
          .then(
            (result) => {
              this.setState({
                isLoaded: true,
                items: result.items
              });
            },
            
            (error) => {
              this.setState({
                isLoaded: true,
                error
              });
            }
          )
    }

    render() {
      const { error, isLoaded } = this.state;
      const { items } = newsList.map(item => {
          if (error) {
            return <div>Error: {error.message}</div>;
          } else if (!isLoaded) {
              return <div>Loading...</div>;
          } else {
              return (
                <ul>
                    <li>{item.articles.source.name}</li>
                </ul>
              );
            }
      })
      return(
        <div className="news-list">
           { items }
        </div>
      )
    }
}

export default News;
