import React, { Component } from 'react';
import logo from './images/jmLogo.png';
import Blogposts from './Blogposts';
import News from './News';

class Home extends Component {
    /*state = {
        blogposts: [
          { date: '04/04/2019', title: 'Why do we wake up early?', desc: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed nulla pariatur non proident, sunt in culpa qui officia deserunt mollit anim id est laborumimage", image: wakeup, id: 1},
          { date: '03/06/2019', title: 'The Benefits of Learning to Code', desc: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed nulla pariatur non proident, sunt in culpa qui officia deserunt mollit anim id est laborumimage", image: code, id: 2},
          { date: '04/26/2018', title: 'Technology Today', desc: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed nulla pariatur non proident, sunt in culpa qui officia deserunt mollit anim id est laborumimage", image: tech, id: 3}
        ]
    }*/

    /*import wakeup from './images/wakeup.png';
import code from './images/coding.png';
import tech from './images/techtoday.jpg';*/

    render() {
        return (
            <div className="container">
                <header className="home-image">
                    <div className="header-logo">
                        <img src={logo}></img>
                    </div>
                    <div className="header-words">A Better Way to Blog</div>
                </header>

                <div className="main">
                    <div className="main-blog">
                        <Blogposts blogs = { this.state.blogs } />
                    </div>
                </div>

                <div className="news-info">
                    <News items = { this.state.items } />
                </div>

                <footer className="footer-container">
                    <p id="footer">Copyright @ 2019 JM.com</p>
                </footer>
            </div>
        )
    } 
}

export default Home
